mapper.select:
SELECT o.order_no,u.nickname AS userNickname,m.name AS movieName, c.name AS cinemaName,s.hall_name, o.seat_info, o.ticket_count, o.amount, o.status,o.pay_time, o.cancel_time, o.create_time, o.update_time FROM `order` o LEFT JOIN user u ON o.user_id = u.id LEFT JOIN screening s ON o.screening_id = s.id LEFT JOIN movie m ON s.movie_id = m.id LEFT JOIN cinema c ON s.cinema_id = c.id
AND o.order_no = #{orderNo}
AND o.status = #{status}
ORDER BY o.create_time DESC
